-- PASO 1: Habilitar la extensión
CREATE EXTENSION IF NOT EXISTS vector;

-- PASO 2: Crear las tablas (¡MODIFICADO A 384 DIMENSIONES!)
CREATE TABLE licitacion (
  id SERIAL PRIMARY KEY,
  entidad VARCHAR(255) NOT NULL,
  objeto TEXT,
  cuantia NUMERIC(18,2),
  modalidad VARCHAR(255),
  numero VARCHAR(255),
  estado VARCHAR(100),
  fecha_public DATE,
  ubicacion VARCHAR(255),
  act_econ VARCHAR(255),
  enlace TEXT,
  portal_origen VARCHAR(255),
  nombre_licitacion_embedding VECTOR(384), -- CAMBIADO
  texto_indexado TEXT
);

CREATE TABLE chunks (
  id SERIAL PRIMARY KEY,
  licitacion_id INT NOT NULL REFERENCES licitacion(id) ON DELETE CASCADE,
  texto TEXT NOT NULL,
  embedding VECTOR(384) NOT NULL -- CAMBIADO
);

CREATE TABLE flags (
  id SERIAL PRIMARY KEY,
  codigo VARCHAR(10) UNIQUE,
  nombre VARCHAR(255),
  descripcion TEXT
);

CREATE TABLE flags_licitaciones (
  id SERIAL PRIMARY KEY,
  licitacion_id INT REFERENCES licitacion(id) ON DELETE CASCADE,
  flag_id INT REFERENCES flags(id) ON DELETE CASCADE,
  valor BOOLEAN DEFAULT FALSE,
  fecha_detectado TIMESTAMP DEFAULT NOW(),
  comentario TEXT,
  fuente TEXT
);

CREATE TABLE flags_log (
  id SERIAL PRIMARY KEY,
  flags_licitaciones_id INT REFERENCES flags_licitaciones(id) ON DELETE CASCADE,
  cambio TEXT,
  fecha TIMESTAMP DEFAULT NOW(),
  usuario TEXT
);

